(function() {

  var setup = {};
  var images;

  var getAllImages = function() {
    if (null === images) {
      require('fs').readdir(setup.imgFolder, function(err, files) {
        if (err) {
          console.log(err);
        }
        else {
          images = {base: setup.pubImgFolder, f: files};
        }
      });
    }
    return images;
  };

  var randImg = function() {
    return images[Math.floor(Math.random()*images.length)];
  };

  module.exports={setup: setup, randomImg: randomImg};
})();
