(function() {

  /**
   * imgFolder: The internal imagefolder
   * pubImgFolder: The relative path for the images
   */
  var config = {};

  var setup = function(c) {
    config = c;
  }

  var getAllImgs = function(done) {
    require('fs').readdir(config.imgFolder, function(err, files) {
      if (err) {
        console.log(err);
      }
      else {
        done({base: config.pubImgFolder, f: files});
      }
    });
  };

  module.exports={setup: setup, getAllImgs: getAllImgs};
})();
